1. SELECT Equipment.EquipmentID, Equipment.Make, Equipment.Model, Equipment.Description FROM Equipment LEFT JOIN EquipmentLoan ON Equipment.EquipmentID=EquipmentLoan.EquipmentID WHERE Damaged = 0 AND (Type LIKE '%Video%' OR Description LIKE '%video%') AND (EquipmentLoan.Current = 0 OR EquipmentLoan.Current IS NULL);
2. SELECT Employee.EmployeeID, Employee.FirstName, Employee.LastName FROM Employee LEFT JOIN Contract ON Employee.EmployeeID = Contract.EmployeeID WHERE Contract.DueFinish LIKE '%2019%';
3. SELECT Project.Title, IF(Expense.Amount > 0, Expense.Amount, 'Null') as TotalExpense FROM Project LEFT JOIN Expense ON Expense.ProjectID = Project.ProjectID GROUP BY Project.ProjectID;
4. SELECT Employee.FirstName, Employee.LastName, Employee.Email, Grade.Title FROM Employee LEFT JOIN EmployeeSkill on EmployeeSkill.EmployeeID = Employee.EmployeeID LEFT JOIN Grade ON Grade.GradeID = Employee.GradeID LEFT JOIN Skill on Skill.SkillID = EmployeeSkill.SkillID where Skill.Title LIKE '%Video%' GROUP BY Employee.EmployeeID;
5. SELECT Project.Title, COUNT(Assignment.EmployeeID) as Test FROM Project LEFT JOIN Assignment ON Project.ProjectID = Assignment.ProjectID LEFT JOIN Employee on Assignment.EmployeeID = Employee.EmployeeID WHERE Project.Internal = 0 AND Employee.Current = 1;
6. SELECT Employee.EmployeeID, Employee.FirstName, Employee.LastName FROM Employee LEFT JOIN Assignment ON Employee.EmployeeID = Assignment.EmployeeID WHERE IF (Employee.EmployeeID = Assignment.EmployeeID,'',Employee.EmployeeID);
7. SELECT Skill.Title AS 'Skilll Name', COUNT(EmployeeSkill.EmployeeID) AS 'Number of Skilled Employees' FROM Skill RIGHT JOIN `EmployeeSkill` ON Skill.SkillID = EmployeeSkill.SkillID GROUP BY EmployeeSkill.SkillID;
8. SELECT Employee.EmployeeID, COUNT(EquipmentLoan.EquipmentID) as 'Equipment Loans' FROM Employee RIGHT JOIN EquipmentLoan on Employee.EmployeeID = EquipmentLoan.EmployeeID WHERE EquipmentLoan.Current = 1 GROUP BY EquipmentLoan.EmployeeID;
9. SELECT Employee.EmployeeID, COUNT(EquipmentLoan.EquipmentID) as 'Equipment Loans' FROM Employee RIGHT JOIN EquipmentLoan on Employee.EmployeeID = EquipmentLoan.EmployeeID GROUP BY EquipmentLoan.EmployeeID;
10. SELECT  Payslip.EmployeeID, Payslip.PayslipID FROM Payslip LEFT JOIN Contract ON Payslip.EmployeeID = Contract.EmployeeID WHERE (Payslip.Payday >= '2017-01-01' AND Payslip.Payday <= '2017-12-31') AND Contract.DueFinish IS NULL;